package com.example.cards

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.cards.databinding.FragmentCountriesBinding

class CountriesFragment : Fragment() {
    private var _binding: FragmentCountriesBinding? = null
    private val binding get() = _binding!!

    // This will hold the name of the continent passed from the previous fragment
    private lateinit var continentName: String

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        // Retrieve the continent name from the arguments
        continentName = arguments?.let { CountriesFragmentArgs.fromBundle(it).continent.name } ?: ""
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        _binding = FragmentCountriesBinding.inflate(inflater, container, false)
        return binding.root
    }
    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        displayCountries()
    }

    private fun displayCountries() {
        // Here you will need to fetch the countries based on the continent name
        val countries = getCountriesByContinent(continentName)

        // Setup RecyclerView to display the countries
        val adapter = CountriesAdapter(countries) { selectedCountry ->
            // Handle country selection if needed
        }
        binding.recyclerViewCountries.layoutManager = LinearLayoutManager(context)
        binding.recyclerViewCountries.adapter = adapter
    }

    private fun getCountriesByContinent(continentName: String): List<Country> {
        return when (continentName) {
            "Africa" -> listOf(
                Country("Nigeria", "Description of Nigeria", "data:image/jpeg;base64,"),
                Country("Egypt", "Description of Egypt", "data:image/jpeg;base64,")

            )
            "Asia" -> listOf(
                Country("China", "Description of China", "data:image/jpeg;base64,"),
                Country("India", "Description of India", "data:image/jpeg;base64,")

            )
            else -> emptyList()
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}
